x = -4:0.01:4;y = x.^2;plot(x,y);% Add Title, Labels, Grid Lines and Scaling on the Graphx = (0:0.1:10);y = sin(x);plot(x,y), xlabel('x'), ylabel('Sin(x)'),title('Sin(x) Graph'), grid on, axis equal;% Draw Multiple Functions on the same Graph% with color optionalx = (0:0.1:10);y = sin(x);g = cos(x);plot(x, y, x, g, '.-'), legend('Sin(x)', 'Cos(x)'), grid on;% plot(x, y, x, g, 'r+');% plot(x, y, 'md')% plot(X,Y,LineSpec)% http://cn.mathworks.com/help/matlab/ref/plot.html#inputarg_LineSpec% http://www.tutorialspoint.com/matlab/matlab_plotting.htm% Set Axis Scales% axis ( [xmin xmax ymin ymax] )x = (0: 0.1: 10);y = exp(-x) .* sin(2*x + 3);plot(x, y), axis([0, 10, -1, 1]);% Generate Sub-Plots% subplot(m, n, p)% ---> divide the Figure Window into m-row, n-column Window% and p means to plot it in pth Windowx = (0: 0.1: 5);y = x.^2;subplot(1, 2, 1);plot(x, y), xlabel('x'), ylabel('x^2'), axis([0, 5, -10, 10]), grid on;y = log2(x);subplot(1, 2, 2);plot(x, y), xlabel('x'), ylabel('x^3'), axis([0, 5, -10, 10]), grid on;